import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import mathias from '../img/mathias.jpg';
import dipa from '../img/dipa.jpg';
import oemer from '../img/oemer.jpg';
import Reacticon from '../img/React-icon.png';
import Materialicon from '../img/React-icon.png';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 240,
  },
});

export default function MediaCard() {
  const classes = useStyles();

  return (
      <> 
     <Grid container spacing={2} className={classes.gridContainer}>
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={mathias}
          title="Matthias Schmettow"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
           Matthias
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
          Visit us on <a href="https://github.com/MatthiasvsGitHub" target="_blank" rel="noreferrer">GitHub</a>
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>

    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={dipa}
          title="Dipali Bedarkar"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            Dipali
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
          Visit us on <a href="https://github.com/dipalibedarkar" target="_blank" rel="noreferrer" >GitHub</a>
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>

    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={oemer}
          title="Ömer Ügür"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            Ömer
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Visit us on <a href="https://github.com/oemerueguer" target="_blank" rel="noreferrer">GitHub</a>
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>

    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={Reacticon}
          title="React JS"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
          React 
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
          React is an open-source, front end, JavaScript library for building user interfaces or UI components. 
          It is maintained by Facebook and a community of individual developers and companies. 
          React can be used as a base in the development of single-page or mobile applications.
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
    </Grid>
    </>
  );
}
