import React, {useState} from 'react';
import { makeStyles} from '@material-ui/core/styles';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
import CopyrightIcon from '@material-ui/icons/Copyright';
import FavoriteIcon from '@material-ui/icons/Favorite';
import GitHubIcon from '@material-ui/icons/GitHub';
import Button from '@material-ui/core/Button';
import { blue } from '@material-ui/core/colors';
import MediaCard from './AboutUs';

const useStyles = makeStyles({
  root:{
    Width: 500,
    marginBottom: 1,
    backgroundColor:blue,
  },
});

export default function Footer() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const [about, setAbout] = useState(false)

  return (
    <>
    {about ? <MediaCard /> : <p>Normal Content</p>}  }
    <BottomNavigation color="primary"
      value={value}
      onChange={(event, newValue) => {
        setValue(newValue);
      }}
      showLabels
      className={classes.root}
    >
      <BottomNavigationAction label="Copyright" icon={<CopyrightIcon />} />
      <Button variant="contained"  href="#contained-buttons" onClick={setAbout(!about)}>
  About Us
</Button>
      
 <BottomNAction label="github" icon={<GitHubIcon />} />
    </BottomNavigation>
    </>
  );
}
